mapper.select:
SELECT
U.user_id,
U.organization_information,
U.user_name,
U.profile_img_url
FROM USERS AS U


        WHERE user_id IN (
        SELECT
        from_user_id
        FROM FOLLOWS
        WHERE to_user_id=#{userId}
        );


        WHERE user_id IN (
        SELECT
        to_user_id
        FROM FOLLOWS
        WHERE from_user_id=#{userId}
        );


        WHERE user_id IN (
        SELECT
        to_user_id
        FROM FOLLOWS
        WHERE from_user_id=#{userId}
        ) OR user_id IN (
        SELECT
        from_user_id
        FROM FOLLOWS
        WHERE to_user_id=#{userId}
        );

SELECT from_user_id
FROM FOLLOWS
WHERE to_user_id = #{userId};

