mapper.select:
SELECT
U.user_id,
U.user_name,
U.profile_img_url,
O.name
FROM USERS AS U
JOIN ORGANIZATION O on U.organization_id = O.organization_id


    WHERE user_id IN (
    SELECT
    from_user_id
    FROM FOLLOWS
    WHERE to_user_id=#{userId}
    );


    WHERE user_id IN (
    SELECT
    to_user_id
    FROM FOLLOWS
    WHERE from_user_id=#{userId}
    );

mapper.insert:
UPDATE USERS
SET profile_img_url=#{profileImgUrl}
WHERE user_id = #{userId};
